            case 'statusRejected':
                this.setState((prevState) => ({status: prevState.status === 'rejected' ? null : 'rejected'}))
                break;
            case 'statusPending':
                this.setState((prevState) => ({status: prevState.status === 'pending' ? null : 'pending'}))
                break;
            default:
